create table plans (
	"id" number not null,
	"speciality_id" number not null,
	"profile" varchar2(672) null,
	"profile_code" varchar2(8) null,
	"study_form" varchar2(12) not null,
	constraint check_plans_study_form check ("study_form" in ('заочная', 'очная', 'очно-заочная'))
);


create sequence seq_plans_id
start with 1
increment by 1
nomaxvalue;


create or replace trigger trig_plans_id
before insert on plans
for each row
begin
	select seq_plans_id.nextval
	into :new."id"
	from dual;
end;


